import './expenseitem.css'

function ExpenseItem(props) {
   const month = props.x.date.toLocaleString('en-US',{month:'long'});
   const year = props.x.date.getFullYear();
   const day = props.x.date.toLocaleString('en-US',{day:'2-digit'}); 
   return (
        <div className='expense-item'>
            <div>
            <div>{props.x.date.toLocaleString('en-US',{month:'long'})}</div>
            <div>{year}</div>
            <div>{day}</div>
            </div>
        
        <div className='expense-item__description'>
            <h2>{props.x.title}</h2>
            <div className='expense-item__price'>Rs {props.x.amount}</div>
        </div>
        </div>
    )
}

export default ExpenseItem;
